jQuery(document).ready(function ($) {
  // Initialize the Slick slider if not already initialized
  // Pause videos on slide change
  $('.basicslider').on('beforeChange', function (event, slick, currentSlide, nextSlide) {
    // Pause all videos in the current slide
    $(this)
      .find('.slick-slide')
      .eq(currentSlide)
      .find('iframe, video')
      .each(function () {
        // Stop YouTube iframe
        if ($(this).is('iframe')) {
          const src = $(this).attr('src');
          $(this).attr('src', src); // Reset the iframe source to stop the video
        }

        // Pause HTML5 video
        if ($(this).is('video')) {
          this.pause();
        }
      });
  });
});

